<?php/// la clase se debe llamar igual que el archivoclass admin_kdb extends fs_list_controller{    public function __construct()    {        /// se crea una entrada 'Mi controlador' dentro del menÃº 'Mio'        parent::__construct(__CLASS__, 'Entrada', 'KDB');    }    protected function create_tabs()    {        $this->add_tab('kdb', 'entradas', 'kdb');        $this->add_search_columns('kdb', ['causa','sintoma','solucion','observaciones']);        $this->add_sort_option('kdb', ['idkdb']);        $this->add_sort_option('kdb', ['fecha']);        $this->add_sort_option('kdb', ['sintoma'], 1);                /// filtros        $this->add_filter_date('kdb', 'fecha', 'desde', '>=');        $this->add_filter_date('kdb', 'fecha', 'hasta', '<=');                /// columnas a mostrar        $this->decoration->add_column('kdb', 'sintoma');        $this->decoration->add_column('kdb', 'causa');        $this->decoration->add_column('kdb', 'solucion');        $this->decoration->add_column('kdb', 'fecha', 'date', 'fecha', 'text-right');        $this->decoration->add_row_url('kdb', 'index.php?page=edit_kdb&code=', 'idkdb');                /// colores        $this->decoration->add_row_option('kdb', 'fecha', null, 'warning');                /// botones        $this->add_button('kdb', 'nuevo', 'index.php?page=edit_kdb', 'fa-plus', 'btn-success');    }}