<?php/// la clase se debe llamar igual que el archivoclass edit_kdb extends fs_edit_controller{    public function __construct()    {        /// se crea una entrada 'Mi controlador' dentro del menú 'Mio'        parent::__construct(__CLASS__, 'Entrada', 'KDB', FALSE, FALSE);    }    public function get_model_class_name()    {        return 'kdb';    }    protected function set_edit_columns()    {        $this->form->add_column('sintoma', 'textarea', 'sintoma', 6, true);        $this->form->add_column('causa', 'textarea', 'causa', 6);        $this->form->add_column('solucion', 'textarea', 'solución', 12, true);        $this->form->add_column('observaciones', 'textarea', 'obsevaciones', 6);        $this->form->add_column('fecha', 'date', 'fecha');    }}