<?php/// la clase se debe llamar igual que el archivoclass kdb extends fs_extended_model{    public $idkdb;    public $sintoma;    public $causa;    public $solucion;    public $observaciones;    public function __construct($data = FALSE)    {        parent::__construct('kdb', $data);    }        public function model_class_name()    {        return 'kdb';    }        public function primary_column()    {        return 'idkdb';    }        public function url($type = 'auto')    {        if($type === 'list') {            return 'index.php?page=admin_kdb';        }                return parent::url($type);    }}